import React, {useState,useEffect} from "react";
import api from './api'
import Popup from "./components/popup";
import Nav from "./components/nav";
import Transactions from "./components/transactions";
import SummarizedView from "./components/summarizedView";

const App = () => {
  const [transactions,setTransactions] = useState([]);
  const [startingBalance, setStartingBalance] = useState(0.0)
  const [goalBalance, setGoalBalance] = useState(0.0)

  const fetchTransactions = async () => {
    const response = await(api.get('/transactions/'))
    console.log(response);
    setTransactions(response.data)
  };

  const fetchUserData = async () => {
    const response = await(api.get('/users/1'))
    console.log(response.data.goal_balance);
    setGoalBalance(response.data.goal_balance)
    console.log(goalBalance);
  }

  useEffect(() => {
    fetchTransactions();
    fetchUserData();
  },[]);

  const [formData,setFormData] = useState({
    amount: '',
    category: '',
    description: '',
    is_income: false,
    date: ''
  });



  const handleSubmit = async (formData) => {
    await api.post('/transactions/',formData)
    setFormData(
      {
        amount: '',
        category: '',
        description: '',
        is_income: false,
        date: ''
      }
    )
    fetchTransactions()
  }

  return (
    <>
      <Nav/>
      <SummarizedView/>
      <Popup onSubmit={handleSubmit}/>
      <Transactions transactions={transactions}/>
    </>
  )
}

export default App;
