import React, {useState,useEffect} from "react";
import api from './api'
import Popup from "./components/popup";
import Nav from "./components/nav";
import Transactions from "./components/transactions";
import SummarizedView from "./components/summarizedView";

const App = () => {
  const [transactions,setTransactions] = useState([]);
  const [startingBalance, setStartingBalance] = useState(0.0)
  const [goalBalance, setGoalBalance] = useState(0.0)
  const [expenses, setTotalExpenses] = useState(0.0)
  const [income, setTotalIncome] = useState(0.0)

  const fetchTransactions = async () => {
    const response = await(api.get('/transactions/'))
    console.log(response);
    setTransactions(response.data)    
  };

  const fetchUserData = async () => {
    const response = await(api.get('/users/1'))
    const goal = response.data.goal_balance;
    const starting = response.data.starting_balance;

    setGoalBalance(goal)
    setStartingBalance(starting)
  }

  useEffect(() => {
    fetchTransactions();
    fetchUserData();
  },[]);

  const [formData,setFormData] = useState({
    amount: '',
    category: '',
    description: '',
    is_income: false,
    date: ''
  });

  useEffect(() => {
    if (transactions.length > 0) {
      const expenses = transactions.filter(transaction => !transaction.is_income);
      const income = transactions.filter(transaction => transaction.is_income);


      const total_expenses = expenses.reduce((acc, curr) => acc + curr.amount, 0);
      setTotalExpenses(total_expenses);

      const total_income = expenses.reduce((acc, curr) => acc + curr.amount, 0);
      setTotalIncome(total_income);
    }
  }, [transactions]);



  const handleSubmit = async (formData) => {
    await api.post('/transactions/',formData)
    setFormData(
      {
        amount: '',
        category: '',
        description: '',
        is_income: false,
        date: ''
      }
    )
    fetchTransactions()
  }

  return (
    <>
      <Nav/>
      <SummarizedView goal={goalBalance} starting={startingBalance} debt={expenses}/>
      <Popup onSubmit={handleSubmit}/>
      <Transactions transactions={transactions}/>
    </>
  )
}

export default App;
